import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { BrainwaveSelector, BrainwaveMode } from './components/BrainwaveSelector';
import { Navigation } from './components/Navigation';

function App() {
  const [brainwaveMode, setBrainwaveMode] = useState<BrainwaveMode>('alpha');
  const [currentSection, setCurrentSection] = useState('movement-lab');

  return (
    <div className="min-h-screen bg-black text-white">
      {/* Navigation */}
      <Navigation
        currentSection={currentSection}
        onSectionChange={setCurrentSection}
        brainwaveMode={brainwaveMode}
      />

      {/* Brainwave Selector */}
      <BrainwaveSelector
        currentMode={brainwaveMode}
        onModeChange={setBrainwaveMode}
      />

      {/* Main Content */}
      <div className="ml-64 flex items-center justify-center min-h-screen">
        <div className="text-center">
          <motion.h1 
            className="text-4xl font-bold mb-4"
            animate={{ opacity: [0.8, 1, 0.8] }}
            transition={{ duration: 3, repeat: Infinity }}
          >
            SpiritLink OS
          </motion.h1>
          <p className="text-gray-400">Consciousness Evolution Platform</p>
          <p className="text-sm text-gray-500 mt-4">Current Mode: {brainwaveMode}</p>
          <p className="text-sm text-gray-500">Current Section: {currentSection}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
