import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface MovementData {
  x: number;
  y: number;
  intensity: number;
  timestamp: number;
}

interface MovementLabProps {
  brainwaveMode: string;
}

export const MovementLab: React.FC<MovementLabProps> = ({ brainwaveMode }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [movements, setMovements] = useState<MovementData[]>([]);
  const [collectiveActivity, setCollectiveActivity] = useState(0);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const videoRef = useRef<HTMLVideoElement>(null);

  // Simulate collective activity
  useEffect(() => {
    const interval = setInterval(() => {
      setCollectiveActivity(Math.random() * 100);
    }, 2000);
    return () => clearInterval(interval);
  }, []);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setIsRecording(true);
      }
    } catch (error) {
      console.log('Camera access denied, using mouse tracking');
      setIsRecording(true);
    }
  };

  const stopRecording = () => {
    setIsRecording(false);
    if (videoRef.current?.srcObject) {
      const stream = videoRef.current.srcObject as MediaStream;
      stream.getTracks().forEach(track => track.stop());
    }
  };

  const handleMouseMove = (e: React.MouseEvent) => {
    if (!isRecording) return;

    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const newMovement: MovementData = {
      x: x / rect.width,
      y: y / rect.height,
      intensity: Math.random(),
      timestamp: Date.now()
    };

    setMovements(prev => [...prev.slice(-50), newMovement]);
  };

  const getBreathingSpeed = () => {
    switch (brainwaveMode) {
      case 'delta': return 6; // Slowest
      case 'theta': return 5;
      case 'alpha': return 4;
      case 'beta': return 3;
      case 'gamma': return 2; // Fastest
      default: return 4;
    }
  };

  return (
    <div className="min-h-screen bg-black text-white p-4">
      {/* Header */}
      <div className="max-w-4xl mx-auto mb-8">
        <motion.h1 
          className="text-4xl font-bold mb-2"
          animate={{ opacity: [0.8, 1, 0.8] }}
          transition={{ duration: getBreathingSpeed(), repeat: Infinity }}
        >
          Movement Lab
        </motion.h1>
        <p className="text-gray-400">Express consciousness through movement</p>
      </div>

      <div className="max-w-4xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Main Movement Area */}
        <div className="lg:col-span-2">
          <motion.div
            className="relative bg-gray-900 rounded-2xl p-6 border border-gray-800"
            onMouseMove={handleMouseMove}
            whileHover={{ scale: 1.02 }}
            transition={{ duration: 0.3 }}
          >
            {/* Breathing Background */}
            <motion.div
              className="absolute inset-0 bg-gradient-to-br from-gray-800 to-gray-900 rounded-2xl"
              animate={{
                opacity: [0.3, 0.6, 0.3]
              }}
              transition={{
                duration: getBreathingSpeed(),
                repeat: Infinity,
                ease: "easeInOut"
              }}
            />

            {/* Movement Canvas */}
            <canvas
              ref={canvasRef}
              className="w-full h-96 rounded-xl border border-gray-700"
              style={{ background: 'transparent' }}
            />

            {/* Recording Controls */}
            <div className="absolute bottom-4 left-4 flex gap-4">
              <motion.button
                className={`px-6 py-2 rounded-full border-2 font-medium ${
                  isRecording 
                    ? 'bg-red-600 border-red-500 text-white' 
                    : 'bg-white text-black border-white'
                }`}
                onClick={isRecording ? stopRecording : startRecording}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                {isRecording ? 'Stop Recording' : 'Start Recording'}
              </motion.button>
            </div>

            {/* Movement Stats */}
            <div className="absolute top-4 right-4 bg-black bg-opacity-50 rounded-lg p-3">
              <div className="text-sm text-gray-400">Movements</div>
              <div className="text-2xl font-bold">{movements.length}</div>
            </div>
          </motion.div>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Collective Activity */}
          <motion.div 
            className="bg-gray-900 rounded-xl p-6 border border-gray-800"
            animate={{ opacity: [0.8, 1, 0.8] }}
            transition={{ duration: 3, repeat: Infinity }}
          >
            <h3 className="text-lg font-semibold mb-4">Collective Activity</h3>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-gray-400">Active Users</span>
                <span className="font-bold">{Math.floor(collectiveActivity)}</span>
              </div>
              <div className="w-full bg-gray-800 rounded-full h-2">
                <motion.div
                  className="bg-white h-2 rounded-full"
                  initial={{ width: 0 }}
                  animate={{ width: `${collectiveActivity}%` }}
                  transition={{ duration: 0.5 }}
                />
              </div>
            </div>
          </motion.div>

          {/* Movement Types */}
          <div className="bg-gray-900 rounded-xl p-6 border border-gray-800">
            <h3 className="text-lg font-semibold mb-4">Movement Types</h3>
            <div className="space-y-3">
              {[
                { name: 'Breath', icon: 'ðŸ«', active: true },
                { name: 'Thought', icon: 'ðŸ’­', active: false },
                { name: 'Emotion', icon: 'ðŸ’«', active: true },
                { name: 'Body', icon: 'ðŸ§˜', active: false },
                { name: 'Art', icon: 'ðŸŽ¨', active: true }
              ].map((type, index) => (
                <motion.div
                  key={type.name}
                  className={`flex items-center p-3 rounded-lg cursor-pointer transition-colors ${
                    type.active ? 'bg-white text-black' : 'bg-gray-800 text-white'
                  }`}
                  whileHover={{ x: 4 }}
                  whileTap={{ scale: 0.98 }}
                >
                  <span className="text-xl mr-3">{type.icon}</span>
                  <span className="font-medium">{type.name}</span>
                </motion.div>
              ))}
            </div>
          </div>

          {/* Live Broadcast */}
          <motion.div 
            className="bg-gray-900 rounded-xl p-6 border border-gray-800"
            animate={{ borderColor: ['#404040', '#ffffff', '#404040'] }}
            transition={{ duration: 2, repeat: Infinity }}
          >
            <h3 className="text-lg font-semibold mb-4">Live Broadcast</h3>
            <div className="space-y-3">
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse" />
                <span className="text-sm text-gray-400">Movement Session</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-blue-500 rounded-full animate-pulse" />
                <span className="text-sm text-gray-400">Collective Research</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-purple-500 rounded-full animate-pulse" />
                <span className="text-sm text-gray-400">Consciousness Evolution</span>
              </div>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Hidden video element for camera access */}
      <video
        ref={videoRef}
        autoPlay
        playsInline
        muted
        className="hidden"
      />
    </div>
  );
};
