import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface NavigationProps {
  currentSection: string;
  onSectionChange: (section: string) => void;
  brainwaveMode: string;
}

const navigationItems = [
  {
    id: 'movement-lab',
    name: 'Movement Lab',
    icon: '🎨',
    description: 'Express consciousness through movement',
    color: 'from-purple-500 to-pink-500'
  },
  {
    id: 'research-feed',
    name: 'Research Feed',
    icon: '📊',
    description: 'Collective consciousness evolution',
    color: 'from-blue-500 to-cyan-500'
  },
  {
    id: 'broadcast',
    name: 'Broadcast',
    icon: '📡',
    description: 'Live consciousness sharing',
    color: 'from-green-500 to-emerald-500'
  },
  {
    id: 'courses',
    name: 'Courses',
    icon: '📚',
    description: 'Structured consciousness learning',
    color: 'from-yellow-500 to-orange-500'
  },
  {
    id: 'collective-intelligence',
    name: 'Collective Intelligence',
    icon: '🧠',
    description: 'AI-powered pattern recognition',
    color: 'from-red-500 to-pink-500'
  },
  {
    id: 'research-journal',
    name: 'Research Journal',
    icon: '📝',
    description: 'Personal consciousness documentation',
    color: 'from-indigo-500 to-purple-500'
  }
];

export const Navigation: React.FC<NavigationProps> = ({
  currentSection,
  onSectionChange,
  brainwaveMode
}) => {
  const [isCollapsed, setIsCollapsed] = useState(false);

  const getAnimationSpeed = () => {
    switch (brainwaveMode) {
      case 'delta': return 0.8;
      case 'theta': return 1.2;
      case 'alpha': return 1;
      case 'beta': return 1.5;
      case 'gamma': return 2;
      default: return 1;
    }
  };

  return (
    <div className="fixed left-0 top-0 h-full bg-black border-r border-gray-800 z-40">
      <motion.div
        className={`h-full bg-gray-900 transition-all duration-300 ${
          isCollapsed ? 'w-16' : 'w-64'
        }`}
        animate={{ 
          width: isCollapsed ? 64 : 256,
          opacity: [0.9, 1, 0.9]
        }}
        transition={{ 
          duration: 0.3,
          opacity: { duration: 3, repeat: Infinity }
        }}
      >
        {/* Header */}
        <div className="p-4 border-b border-gray-800">
          <div className="flex items-center justify-between">
            {!isCollapsed && (
              <motion.div
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.3 }}
              >
                <h1 className="text-xl font-bold text-white">SpiritLink OS</h1>
                <p className="text-xs text-gray-400">Consciousness Evolution Platform</p>
              </motion.div>
            )}
            <motion.button
              className="p-2 rounded-lg hover:bg-gray-800 transition-colors"
              onClick={() => setIsCollapsed(!isCollapsed)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <span className="text-white text-lg">
                {isCollapsed ? '→' : '←'}
              </span>
            </motion.button>
          </div>
        </div>

        {/* Navigation Items */}
        <div className="p-2 space-y-2">
          {navigationItems.map((item, index) => (
            <motion.div
              key={item.id}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <motion.button
                className={`w-full p-3 rounded-lg transition-all duration-200 flex items-center space-x-3 ${
                  currentSection === item.id
                    ? 'bg-white text-black shadow-lg'
                    : 'text-white hover:bg-gray-800'
                }`}
                onClick={() => onSectionChange(item.id)}
                whileHover={{ x: 4 }}
                whileTap={{ scale: 0.98 }}
                animate={currentSection === item.id ? {
                  scale: [1, 1.02, 1],
                  opacity: [0.9, 1, 0.9]
                } : {}}
                transition={{ 
                  duration: 2 * getAnimationSpeed(),
                  repeat: currentSection === item.id ? Infinity : 0
                }}
              >
                <div className="text-2xl">{item.icon}</div>
                {!isCollapsed && (
                  <div className="flex-1 text-left">
                    <div className="font-medium">{item.name}</div>
                    <div className="text-xs opacity-70">{item.description}</div>
                  </div>
                )}
              </motion.button>
            </motion.div>
          ))}
        </div>

        {/* Bottom Section */}
        <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-gray-800">
          {!isCollapsed && (
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: 0.5 }}
            >
              <div className="space-y-3">
                {/* Collective Activity */}
                <div className="bg-gray-800 rounded-lg p-3">
                  <div className="flex items-center justify-between text-sm">
                    <span className="text-gray-400">Collective Activity</span>
                    <span className="text-white font-semibold">87%</span>
                  </div>
                  <div className="w-full bg-gray-700 rounded-full h-1 mt-2">
                    <motion.div
                      className="bg-white h-1 rounded-full"
                      initial={{ width: 0 }}
                      animate={{ width: '87%' }}
                      transition={{ duration: 1 }}
                    />
                  </div>
                </div>

                {/* Breakthrough Alert */}
                <motion.div
                  className="bg-gradient-to-r from-yellow-500 to-orange-500 rounded-lg p-3"
                  animate={{ 
                    scale: [1, 1.02, 1],
                    opacity: [0.8, 1, 0.8]
                  }}
                  transition={{ duration: 2, repeat: Infinity }}
                >
                  <div className="flex items-center space-x-2">
                    <span className="text-lg">🔥</span>
                    <div className="text-sm">
                      <div className="font-semibold">Breakthrough Detected</div>
                      <div className="text-xs opacity-90">1,247 users synchronized</div>
                    </div>
                  </div>
                </motion.div>
              </div>
            </motion.div>
          )}
        </div>
      </motion.div>
    </div>
  );
};
