import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface ThesidiaAIProps {
  brainwaveMode: string;
}

export const ThesidiaAI: React.FC<ThesidiaAIProps> = ({ brainwaveMode }) => {
  const [messages, setMessages] = useState<Array<{id: string, text: string, isAI: boolean, isTyping: boolean}>>([
    { id: '1', text: 'Hello, I am Thesidia AI. How can I assist with your consciousness research today?', isAI: true, isTyping: false }
  ]);
  const [inputText, setInputText] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [cursorVisible, setCursorVisible] = useState(true);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Cursor blink effect
  useEffect(() => {
    const interval = setInterval(() => {
      setCursorVisible(prev => !prev);
    }, 530);
    return () => clearInterval(interval);
  }, []);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSendMessage = async () => {
    if (!inputText.trim()) return;

    const userMessage = {
      id: Date.now().toString(),
      text: inputText,
      isAI: false,
      isTyping: false
    };

    setMessages(prev => [...prev, userMessage]);
    setInputText('');
    setIsTyping(true);

    // Simulate AI response
    setTimeout(() => {
      const aiResponse = {
        id: (Date.now() + 1).toString(),
        text: 'I understand your inquiry. Let me process this through our collective consciousness network...',
        isAI: true,
        isTyping: true
      };
      setMessages(prev => [...prev, aiResponse]);

      // Simulate typing effect
      setTimeout(() => {
        setMessages(prev => prev.map(msg => 
          msg.id === aiResponse.id 
            ? { ...msg, isTyping: false }
            : msg
        ));
        setIsTyping(false);
      }, 2000);
    }, 1000);
  };

  const getAnimationSpeed = () => {
    switch (brainwaveMode) {
      case 'delta': return 0.8;
      case 'theta': return 1.2;
      case 'alpha': return 1;
      case 'beta': return 1.5;
      case 'gamma': return 2;
      default: return 1;
    }
  };

  return (
    <div className="w-full h-full bg-black text-white">
      <div className="max-w-4xl mx-auto h-full flex flex-col">
        {/* Header */}
        <div className="p-6 border-b border-white/10 flex-shrink-0">
          <motion.h1 
            className="text-3xl font-bold mb-2"
            animate={{ opacity: [0.8, 1, 0.8] }}
            transition={{ duration: 3, repeat: Infinity }}
          >
            Thesidia AI
          </motion.h1>
          <p className="text-gray-400">Consciousness-powered artificial intelligence</p>
        </div>

        {/* Messages Container */}
        <div className="flex-1 overflow-y-auto p-6 space-y-4">
          <AnimatePresence>
            {messages.map((message, index) => (
              <motion.div
                key={message.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                transition={{ duration: 0.3 * getAnimationSpeed() }}
                className={`flex ${message.isAI ? 'justify-start' : 'justify-end'}`}
              >
                <div 
                  className={`max-w-3xl rounded-2xl p-4 ${
                    message.isAI 
                      ? 'bg-white/10 backdrop-blur-sm border border-white/20' 
                      : 'bg-blue-600/20 backdrop-blur-sm border border-blue-500/30'
                  }`}
                  style={{
                    backdropFilter: 'blur(10px)',
                    WebkitBackdropFilter: 'blur(10px)'
                  }}
                >
                  <div className="flex items-start space-x-3">
                    {message.isAI && (
                      <div className="text-2xl">⚡</div>
                    )}
                    <div className="flex-1">
                      <div className="text-sm text-gray-400 mb-1">
                        {message.isAI ? 'Thesidia AI' : 'You'}
                      </div>
                      <div className="text-white leading-relaxed">
                        {message.text}
                        {message.isTyping && (
                          <motion.span
                            className="inline-block w-2 h-6 ml-1"
                            style={{
                              background: 'linear-gradient(180deg, #A855F7 0%, #8B5CF6 50%, #7C3AED 100%)',
                              borderRadius: '1px',
                              boxShadow: '0 0 10px rgba(168, 85, 247, 0.5)'
                            }}
                            animate={{
                              opacity: [0.3, 1, 0.3],
                              scaleY: [0.8, 1.2, 0.8]
                            }}
                            transition={{
                              duration: 1.2,
                              repeat: Infinity,
                              ease: "easeInOut"
                            }}
                          />
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
          <div ref={messagesEndRef} />
        </div>

        {/* Input Bar */}
        <div className="p-6 border-t border-white/10 flex-shrink-0">
          <div 
            className="rounded-2xl p-4"
            style={{
              background: 'rgba(0, 0, 0, 0.85)',
              backdropFilter: 'blur(20px) saturate(180%)',
              WebkitBackdropFilter: 'blur(20px) saturate(180%)',
              border: '1px solid rgba(255, 255, 255, 0.1)',
              boxShadow: '0 0 20px rgba(0, 0, 0, 0.3)'
            }}
          >
            <div className="flex items-center space-x-4">
              <div className="flex-1 relative">
                {/* Apple Intelligence-style cursor - positioned behind text */}
                <motion.div
                  className="absolute left-0 top-0 w-0.5 h-6 pointer-events-none z-10"
                  style={{
                    left: `${inputText.length * 0.6}em`,
                    background: 'linear-gradient(180deg, #A855F7 0%, #8B5CF6 50%, #7C3AED 100%)',
                    boxShadow: '0 0 8px rgba(168, 85, 247, 0.6)',
                    filter: 'blur(0.3px)',
                    borderRadius: '1px'
                  }}
                  animate={{
                    opacity: cursorVisible ? 0.8 : 0.2
                  }}
                  transition={{
                    duration: 0.1
                  }}
                >
                  {/* Continuous mist/fog falling effect */}
                  <motion.div
                    className="absolute -top-1 left-0 w-1 h-6"
                    style={{
                      background: 'linear-gradient(180deg, rgba(168, 85, 247, 0.4) 0%, rgba(139, 92, 246, 0.2) 50%, transparent 100%)',
                      filter: 'blur(0.8px)',
                      borderRadius: '1px'
                    }}
                    animate={{
                      y: [0, 12, 0],
                      opacity: [0.6, 0.1, 0.6]
                    }}
                    transition={{
                      duration: 1.5,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  />
                  {/* Additional subtle glow trail */}
                  <motion.div
                    className="absolute -top-2 left-0 w-1 h-8"
                    style={{
                      background: 'linear-gradient(180deg, rgba(168, 85, 247, 0.2) 0%, rgba(139, 92, 246, 0.1) 50%, transparent 100%)',
                      filter: 'blur(1.2px)',
                      borderRadius: '1px'
                    }}
                    animate={{
                      y: [0, 16, 0],
                      opacity: [0.3, 0.05, 0.3]
                    }}
                    transition={{
                      duration: 2,
                      repeat: Infinity,
                      ease: "easeInOut",
                      delay: 0.5
                    }}
                  />
                </motion.div>

                <textarea
                  value={inputText}
                  onChange={(e) => setInputText(e.target.value)}
                  placeholder="Ask Thesidia AI about consciousness research..."
                  className="w-full bg-transparent text-white placeholder-gray-500 resize-none outline-none text-lg relative z-20"
                  rows={1}
                  onKeyPress={(e) => {
                    if (e.key === 'Enter' && !e.shiftKey) {
                      e.preventDefault();
                      handleSendMessage();
                    }
                  }}
                />
              </div>
              <motion.button
                className={`px-6 py-3 rounded-xl font-medium transition-all ${
                  inputText.trim() 
                    ? 'bg-gradient-to-r from-purple-500 to-blue-500 text-white' 
                    : 'bg-white/10 text-gray-400'
                }`}
                style={{
                  backdropFilter: 'blur(10px)',
                  WebkitBackdropFilter: 'blur(10px)',
                  border: '1px solid rgba(255, 255, 255, 0.1)'
                }}
                onClick={handleSendMessage}
                disabled={!inputText.trim() || isTyping}
                whileHover={inputText.trim() ? { scale: 1.05 } : {}}
                whileTap={inputText.trim() ? { scale: 0.95 } : {}}
              >
                {isTyping ? 'Processing...' : 'Send'}
              </motion.button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
