import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface MovementLabProps {
  brainwaveMode: string;
}

interface MovementPost {
  id: string;
  title: string;
  description: string;
  author: string;
  timestamp: string;
  movementType: 'flow' | 'rhythm' | 'stillness' | 'expression' | 'synchronization';
  collectiveImpact: number;
  imageUrl: string;
  videoUrl?: string;
  likes: number;
  shares: number;
  isLive?: boolean;
}

const mockMovementPosts: MovementPost[] = [
  {
    id: '1',
    title: 'Morning Flow Sequence',
    description: 'A gentle morning movement sequence that awakens the body and mind. Perfect for transitioning from sleep to conscious awareness.',
    author: 'Sarah Chen',
    timestamp: '2 hours ago',
    movementType: 'flow',
    collectiveImpact: 89,
    imageUrl: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=600&fit=crop',
    likes: 234,
    shares: 45
  },
  {
    id: '2',
    title: 'Rhythmic Drum Circle',
    description: 'Collective rhythm creation through synchronized drumming. Experience the power of unified movement and sound.',
    author: 'Marcus Rodriguez',
    timestamp: '4 hours ago',
    movementType: 'rhythm',
    collectiveImpact: 92,
    imageUrl: 'https://images.unsplash.com/photo-1516280440614-37939bbacd81?w=400&h=500&fit=crop',
    likes: 567,
    shares: 123,
    isLive: true
  },
  {
    id: '3',
    title: 'Meditative Stillness',
    description: 'Finding peace through complete stillness. A practice of conscious non-movement that reveals inner wisdom.',
    author: 'Zen Master Li',
    timestamp: '6 hours ago',
    movementType: 'stillness',
    collectiveImpact: 78,
    imageUrl: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=700&fit=crop',
    likes: 189,
    shares: 67
  },
  {
    id: '4',
    title: 'Expression Through Dance',
    description: 'Free-form dance as a medium for emotional expression and release. Let your body tell its story.',
    author: 'Aisha Thompson',
    timestamp: '8 hours ago',
    movementType: 'expression',
    collectiveImpact: 85,
    imageUrl: 'https://images.unsplash.com/photo-1516280440614-37939bbacd81?w=400&h=550&fit=crop',
    likes: 342,
    shares: 89
  },
  {
    id: '5',
    title: 'Global Synchronization',
    description: 'Thousands of people moving in harmony across time zones. The power of collective consciousness in motion.',
    author: 'Global Movement Collective',
    timestamp: '12 hours ago',
    movementType: 'synchronization',
    collectiveImpact: 96,
    imageUrl: 'https://images.unsplash.com/photo-1506905925346-21bda4d32df4?w=400&h=650&fit=crop',
    likes: 1247,
    shares: 234
  },
  {
